mapper.select:
SELECT o.* FROM base_org_ip_segment AS oi
INNER JOIN base_koal_org AS o ON oi.area_code = o.`code`
WHERE oi.start_ip_num <=   #{ipNum} AND oi.end_ip_num >=  #{ipNum}

  AND o.org_hierarchy = #{orgHierarchy}


  AND o.`code` = #{code}

ORDER BY oi.start_ip_num DESC,oi.end_ip_num ASC
LIMIT 0,1

choose.when:
select * from user u inner join (select code from base_koal_org where sub_code  like  CONCAT(#{userQuery.subCode},'%'))b
on u.org_code = b.code where u.status <> 2

order by ${userQuery.order_} ${userQuery.by_}
limit #{userQuery.start_},#{userQuery.count_}

choose.otherwise:
select * from user u  where u.status <> 2

order by ${userQuery.order_} ${userQuery.by_}
limit #{userQuery.start_},#{userQuery.count_}

choose.when:
select * from "user" u inner join (select code from base_koal_org where sub_code  like  CONCAT(#{userQuery.subCode},'%'))b
on u.org_code = b.code where u.status <> 2

order by ${userQuery.order_} ${userQuery.by_}
limit #{userQuery.start_},#{userQuery.count_}

choose.otherwise:
select * from "user" u  where u.status <> 2

order by ${userQuery.order_} ${userQuery.by_}
limit #{userQuery.start_},#{userQuery.count_}

mapper.select:
select u.*,uk.serial,uk.public_key as publicKey from user u left join user_ukey uk on u.id = uk.user_id where u.status <> 2

order by ${userQuery.order_} ${userQuery.by_}
limit #{userQuery.start_},#{userQuery.count_}

select u.*,uk.serial,uk.public_key as publicKey from "user" u left join user_ukey uk on u.id = uk.user_id where u.status <> 2

order by ${userQuery.order_} ${userQuery.by_}
limit #{userQuery.start_},#{userQuery.count_}
